///////////////////////////////////////
/// usage of the read argument function
///////////////////////////////////////
void print_usage() {
  cout << endl
       << "Usage:" << endl
       << " -p   <set>     specify PDFs being used (default \"PDF4LHC15_nnlo_100\")" << endl
       << "                installed: NNPDF31_nnlo_as_0118, CT14nnlo, PDF4LHC15_nnlo_100" << endl
       << "                           NNPDF31_nlo_as_0118, CT14nlo, PDF4LHC15_nlo_100 " << endl
	   << "							  MSTW2008nnlo90cl, MMHT2014nlo68cl, GRVPI0" << endl
       << " -Q   <Q>      set transferred momentum Q [GeV]" << endl
       << " -r   <muR>         set renormalization scale [GeV]" << endl
       << " -f   <muF>         set factorization scale [GeV]" << endl
       << " -S   <com> 	    set C.O.M. energy [TeV]" << endl
       << " -C   <proc>  process (1 for DY, 2 for higgs)" << endl
       << " -O   <order>  order (0 for LO, 1 for NLO, 2 for NNLO)" << endl
       << " -R <resum>  resum (0 for no, 1 for yes)" << endl
       << " -P <pdf> 	  usage of pdfs (real = 0, fake = 1)" << endl
       << endl;
  exit(0);
  return;
}

///////////////////////////////////////
/// defaults of the programm
///////////////////////////////////////
void update_defaults(bool printout , bool pdfset){
    Q2 = pow(Q,2);
    muF2 = muF*muF;
    muR2 = muR*muR;
    tau = Q2/S2;
    if(pdfset){
		LHAPDF::PDFSet setk(setname);
		int nmem(0.); //number of members
		vector<int> pids; //number of flavors, span from -5 to 5 with 0 = 21 gluon
		nmem = setk.size()-1;
		pdfs = setk.mkPDFs();
		pids = pdfs[0]->flavors();
		xmin_pdfs = pdfs[0]->xMin();
		xmax_pdfs = pdfs[0]->xMax();
	}
	alphas_muF = pdfs[0]->alphasQ(muF);
	alphas_Q = pdfs[0]->alphasQ(Q);
	alphas_muR = pdfs[0]->alphasQ(muR);
	if(printout){
		cout << "=========================================" << endl;
		cout << "The values are set as follows: " << endl
		<< "PDFset: 				" << setname << endl
		<< "Momentum [GeV]: 			" << Q << endl
		<< "Renormalization scale [GeV]: 		" << muR << endl
		<< "Factorization scale [GeV]:		" << muF << endl
		<< "Center of Mass energy [TeV]: 		" << S/1000. << endl
		<< "tau:					" << tau << endl;
		cout << "alphas_Q:			 	" << alphas_Q << endl;
		cout << "alphas_muF:		 		" << alphas_muF << endl;
		cout << "alphas_muR: 		 		" << alphas_muR << endl;
		if(higgs) cout << "Higgs channel:" << endl;
		if(DY) cout << "DY channel:" << endl;
		if(NNLO) cout << "computing up to NNLO" << endl;
		else if(NLO) cout << "computing up to NLO" << endl;
		else if(LO) cout << "computing up to LO" << endl;
		if(RES) cout << "Resumming LL: "<< (bool) ISLL << ", NLL: "<< (bool) ISNLL << ", NNLL: "<< (bool) ISNNLL << ", NLP: "<< (bool) ISNLP << endl;
		if(realPDF) cout << "Using the real PDFs" << endl;
		if(fitPDF) cout << "Using the fitted PDFs" << endl;
		cout << "=========================================" << endl;
	}
}


///////////////////////////////////////
/// read arguments of command line
///////////////////////////////////////

void read_arguments(int argc, char* argv[]) {
  const char* const short_options = "hp:f:r:Q:S:C:O:R:P:"; //: is needed as there is a variable after the -p, -S etc
  int next_option;
  ostringstream sset;
  while ((next_option = getopt(argc, argv, short_options)) != -1)
  { switch (next_option) {
    case 'h':
      print_usage();
    case 'p':
      sset << optarg;
      setname = sset.str();
      if (setname=="PDF4LHC15_nnlo_100"){fitcoeff = fitcoeff_PDF4LHC15_nnlo_100;}
      else if (setname=="PDF4LHC15_nlo_100"){fitcoeff = fitcoeff_PDF4LHC15_nlo_100;}
      else if (setname=="NNPDF31_nnlo_as_0118"){fitcoeff = fitcoeff_NNPDF31_nnlo_as_0118;}
      else if (setname=="MSTW2008nnlo90cl"){fitcoeff = fitcoeff_MSTW2008nnlo90cl;}
      else {cout << "PDFset " << setname << " not implemented, using default" << endl;}
      break;
    case 'f':
      muF = strtod(optarg, NULL);
      muF2 = muF*muF;
      break;
    case 'r':
      muR = strtod(optarg, NULL);
      muR2 = muR*muR;
      break;
    case 'Q':
      Q = strtod(optarg, NULL);
      muF = Q;
      muR = Q;
      break;
    case 'S':
       S = strtod(optarg, NULL)*1000.;
       S2 = pow(S,2);
      break;
    case 'C':
		{
		   int PROC = (int) strtod(optarg, NULL);
		   if(PROC  == 1){
			   DY = true;
			   higgs = false;
		   }
		   if(PROC  == 2){
			   DY = false;
			   higgs = true;
		   }
      break;
		}
    case 'O':
		{
		   int order = strtod(optarg, NULL);
		   if(order == 0){
			   LO = true;
			   NLO = false;
			   NNLO = false;
		   }
		   else if(order == 1){
			   LO = true;
			   NLO = true;
			   NNLO = false;
		   }
		   else if(order == 2){
			   LO = true;
			   NLO = true;
			   NNLO = true;
	   }
      break;
		}
    case 'R':
		{
		   int order = strtod(optarg, NULL);
		   if(order == 0){
			   RES = true;
		   }
		   else if(order == 1){
			   RES = false;
		   }
		  break;
		}
    case 'P':
		{
		   int order = strtod(optarg, NULL);
		   if(order == 0){
			   realPDF = true;
			   fitPDF=false;
			   RES = false;
		   }
		   else if(order == 1){
			   fitPDF = true;
			   realPDF=false;
		   }
		  break;
		}
    case -1: break;
    case ':': abort();
    }
  }
  tau = Q2/S2; //reset tau
  return;
}
